{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "historyLimit": 10,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.0.0",
        "id": "8316914",
        "label": "Sample 4",
        "remark": "Simple HTTP application referencing an externally hosted iRule",
        "Sample_04": {
            "class": "Tenant",
            "ApplicationA": {
                "class": "Application",
                "template": "http",
                "serviceMain": {
                    "class": "Service_HTTP",
                    "iRules": [
                        "Maintenance_iRule"
                    ],
                    "virtualAddresses": [
                        "10.0.1.170"
                    ],
                    "pool": "web_pool"
                },
                "web_pool": {
                    "class": "Pool",
                    "monitors": [
                        "http"
                    ],
                    "members": [
                        {
                            "servicePort": 80,
                            "serverAddresses": [
                                "192.0.1.170",
                                "192.0.1.171"
                            ]
                        }
                    ]
                },
                "Maintenance_iRule": {
                    "class": "iRule",
                    "iRule": {
                        "url": "https://raw.githubusercontent.com/f5devcentral/f5-automation-labs/master/files/irules/maintenance-page.irule"
                    }
                }
            }
        }
    }
}

