{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.0.0",
        "id": "789ghi",
        "label": "Sample 3",
        "remark": "TCP load-balanced to ICAP with custom monitor",
        "Sample_03": {
            "class": "Tenant",
            "A1": {
                "class": "Application",
                "template": "tcp",
                "serviceMain": {
                    "class": "Service_TCP",
                    "virtualAddresses": [
                        "10.0.5.10"
                    ],
                    "virtualPort": 1344,
                    "pool": "svc_pool"
                },
                "svc_pool": {
                    "class": "Pool",
                    "monitors": [
                        {
                            "use": "icap_monitor"
                        }
                    ],
                    "members": [
                        {
                            "servicePort": 1344,
                            "serverAddresses": [
                                "192.0.5.10",
                                "192.0.5.11"
                            ]
                        }
                    ]
                },
                "icap_monitor": {
                    "class": "Monitor",
                    "monitorType": "tcp",
                    "send": "OPTIONS icap://icap.example.net/ ICAP/1.0\r\nUser-Agent: f5-ADC\r\n\r\n",
                    "receive": "ICAP/1.0 200 OK",
                    "adaptive": false
                }
            }
        }
    }
}

{
    "class": "ADC",
    "schemaVersion": "3.9.0",
    "id": "TEST_ANALYTICS_PROFILE",
    "remark": "Analytics example",
    "Sample_analytics_capture": {
        "class": "Tenant",
        "TEST_ANALYTICS_Profile": {
            "class": "Application",
            "template": "http",
            "serviceMain": {
                "class": "Service_HTTP",
                "virtualAddresses": [
                    "192.0.2.19",
                    "192.0.2.20",
                    "192.0.2.21"
                ],
                "virtualPort": 80,
                "profileAnalytics": {
                    "use": "pA_Sample"
                },
                "pool": "poolRoundRobin"
            },
            "poolRoundRobin": {
                "class": "Pool",
                "loadBalancingMode": "round-robin",
                "monitors": [
                    "http"
                ],
                "members": [
                    {
                        "servicePort": 80,
                        "serverAddresses": [
                            "192.0.2.58",
                            "192.0.2.59",
                            "192.0.2.60"
                        ]
                    }
                ]
            },
            "pA_Sample": {
                "class": "Analytics_Profile",
                "collectedStatsInternalLogging": true,
                "collectedStatsExternalLogging": false,
                "capturedTrafficInternalLogging": true,
                "capturedTrafficExternalLogging": false,
                "captureFilter": {
                    "requestCapturedParts": "body",
                    "responseCapturedParts": "all",
                    "dosActivity": "mitigated-by-dosl7",
                    "capturedProtocols": "http",
                    "capturedReadyForJsInjection": "enabled",
                    "requestContentFilterSearchPart": "body",
                    "requestContentFilterSearchString": "something request something",
                    "responseContentFilterSearchPart": "all",
                    "responseContentFilterSearchString": "something respone something",
                    "virtualServers": [
                        "serviceMain-1"
                    ],
                    "nodeAddresses": [
                        "192.0.2.59"
                    ],
                    "responseCodes": [
                        400,
                        401,
                        402
                    ],
                    "methods": [
                        "GET",
                        "POST",
                        "PUT"
                    ],
                    "urlFilterType": "white-list",
                    "urlPathPrefixes": [
                        "a.org",
                        "b.org",
                        "c.org"
                    ],
                    "userAgentSubstrings": [
                        "Mozilla (01",
                        "Mozilla (02",
                        "Mozilla (03"
                    ],
                    "clientIps": [
                        "10.9.10.10",
                        "10.9.10.11",
                        "10.9.10.12"
                    ]
                }
            }
        }
    }
}

{
    "class": "ADC",
    "updateMode": "selective",
    "schemaVersion": "3.0.0",
    "id": "DNS",
    "Sample_non_http_03": {
        "class": "Tenant",
        "TEST_DNS_Zone": {
            "class": "Application",
            "template": "generic",
            "dnsZone": {
                "class": "DNS_Zone",
                "label": "dnsZone",
                "remark": "DNS Zone test",
                "dnsExpress": {
                    "enabled": true,
                    "nameserver": {
                        "use": "dnsNameserverZone"
                    },
                    "notifyAction": "consume",
                    "allowNotifyFrom": [
                        "10.1.1.1"
                    ],
                    "verifyNotifyTsig": false
                },
                "responsePolicyEnabled": true,
                "serverTsigKey": {
                    "use": "tsigKeyZone"
                },
                "transferClients": [
                    {
                        "use": "dnsNameserverZone"
                    }
                ]
            },
            "dnsNameserverZone": {
                "class": "DNS_Nameserver",
                "label": "dnsNameserverZone",
                "remark": "A DNS Nameserver",
                "address": "10.1.1.2",
                "port": 53,
                "routeDomain": {
                    "bigip": "/Common/0"
                },
                "tsigKey": {
                    "use": "tsigKeyZone"
                }
            },
            "tsigKeyZone": {
                "class": "DNS_TSIG_Key",
                "label": "tsigKeyZone",
                "remark": "TSIG Key test",
                "algorithm": "hmacmd5",
                "secret": {
                    "ciphertext": "ZjVmNQ==",
                    "miniJWE": true,
                    "protected": "eyJhbGciOiJkaXIiLCJlbmMiOiJub25lIn0",
                    "ignoreChanges": true,
                    "allowReuse": false
                }
            }
        }
    },
    "DNS_PROFILE_1": {
        "class": "Tenant",
        "DNS_PROFILE_1": {
            "class": "Application",
            "template": "udp",
            "serviceMain": {
                "class": "Service_UDP",
                "virtualPort": 80,
                "virtualAddresses": [
                    "198.19.192.210"
                ],
                "profileUDP": {
                    "use": "profileUdp"
                },
                "profileDNS": {
                    "use": "profileDnsHW"
                }
            },
            "profileDnsHW": {
                "class": "DNS_Profile",
                "label": "profileDnsHW",
                "remark": "DNS Profile test",
                "parentProfile": {
                    "bigip": "/Common/dns"
                },
                "rapidResponseEnabled": false,
                "rapidResponseLastAction": "allow",
                "hardwareQueryValidationEnabled": true,
                "hardwareResponseCacheEnabled": true,
                "dnssecEnabled": false,
                "globalServerLoadBalancingEnabled": false,
                "dnsExpressEnabled": false,
                "cacheEnabled": false,
                "dns64Mode": "secondary",
                "dns64Prefix": "0:0:0:0:0:0:0:0",
                "dns64AdditionalSectionRewrite": "any",
                "unhandledQueryAction": "drop",
                "localBindServerEnabled": false,
                "zoneTransferEnabled": true,
                "recursionDesiredEnabled": false,
                "securityEnabled": false,
                "loggingEnabled": false,
                "statisticsSampleRate": 20
            },
            "profileUdp": {
                "class": "UDP_Profile",
                "datagramLoadBalancing": true
            }
        }
    }
}

{
